mapper.insert:
INSERT
    INTO
        chat_msg
(
        chat_msg_no
        , chat_rm_no
        , user_id
        , chat_msg_cn
        , reg_dt
        , file_yn
    )
VALUES(
    (SELECT NVL(MAX(CHAT_MSG_NO),0)+1 FROM CHAT_MSG)
    , #{chatRmNo}
    , #{userId}
    , #{chatMsgCn}
    , SYSDATE
    , #{fileYn}
)

update.foreach:
INTO
    CHAT_USERS
(
		  USER_NO
        , CHAT_RM_NO
        , USER_ID
        , HOST_YN
    )
VALUES(
	(SELECT NVL(MAX(USER_NO),0)+1 FROM CHAT_USERS) +#{i}
	, (SELECT MAX(CHAT_RM_NO) FROM CHAT_ROOM)
    , #{item.userId}
    , #{item.hostYn}
)
