update.foreach:

INTO DRFT_OPN (
	DR_OPN_NO
	, DR_NO
	, USER_ID
	, OPNN_CN
	, DR_OPNN_DT )
VALUES(
	#{drOpnNo} + #{idx}
	, #{drNo}
	, #{item.userId}
	, #{item.opnnCn}
	, #{item.drOpnnDt} )

mapper.insert:


	SELECT NVL(MAX(VAC_USE_NO), 0) + 1 FROM VAC_USE


INSERT INTO VAC_USE (
	VAC_USE_NO
		, USER_ID
		, DR_NO
		, CNY_DAY
		, VAC_TP
		, BGNG_DT
		, END_DT
		, VAC_GRNT_NO
		, VAC_RSN )
	VALUES(
	#{draftVacVO.vacUseNo}
	, #{userId}
	, #{drNo}
	, #{draftVacVO.cnyDay}
	, #{draftVacVO.vacTp}
	, #{draftVacVO.bgngDt}
	, #{draftVacVO.endDt}
	, #{draftVacVO.vacGrntNo}
	, #{draftVacVO.vacRsn} )

update.foreach:

INTO DRFT_ATCH (
	DRFT_FL_NO
	, DR_NO
	, FILE_NM
	, FILE_UUID
	, FILE_PATH
	, FILE_EXTN
	, FILE_SIZE )
VALUES (
	#{drftFlNo} + #{idx}
	, #{drNo}
	, #{item.fileNm}
	, #{item.fileUuid}
	, #{item.filePath}
	, #{item.fileExtn}
	, #{item.fileSize} )

INTO DRFT_ATRZ (
	DR_NO
	, USER_ID
	, ATRZ_SN
	, ATRZ_CD
	, ATRZ_STTS_CD
	, ATRZ_DT )
VALUES (
	#{drNo}
	, #{draftAtrzVO.userId}
	, #{draftAtrzVO.atrzSn}
	, #{draftAtrzVO.atrzCd}
	, #{draftAtrzVO.atrzSttsCd}
	, NULL )

mapper.select:
SELECT DR_NO , USER_ID , DR_STTS_CD , DR_DT , DEL_YN , DEL_DT , DRFT_TITLE , FNSH_DT FROM DRFT d2 WHERE d2.USER_ID = #{userId} AND d2.DEL_YN = 'N'
AND DR_STTS_CD = #{drSttsCd}
ORDER BY DR_NO DESC

mapper.update:
UPDATE
	DRFT_ATRZ
SET
	ATRZ_STTS_CD = #{draftAtrzVOList[0].atrzSttsCd}
	, ATRZ_DT = SYSDATE
WHERE
	DR_NO = #{drNo}
	AND USER_ID = #{draftAtrzVOList[0].userId}
