update.foreach:
INTO BBS_ATCH(
	BBS_FL_NO, FILE_NM, FILE_UUID, FILE_PATH, FILE_EXTN, FILE_SIZE, PST_NO, FILE_EXIST)
VALUES(
	  #{bbsAtchVO.bbsFlNo}+#{idx}
	, #{bbsAtchVO.fileNm}
	, #{bbsAtchVO.fileUuid}
	, #{bbsAtchVO.filePath}
	, #{bbsAtchVO.fileExtn}
	, #{bbsAtchVO.fileSize}
	, #{bbsAtchVO.pstNo}
	, 'Y')

mapper.insert:

SELECT SEQ_BBS.NEXTVAL FROM DUAL


INSERT INTO BBS(
	 pst_no
   , user_id
   , co_id
   , delr_nm
   , mdfr_nm
   , pst_hd
   , pst_ttl
   , pst_cn
   , reg_dt
   , mdfcn_dt
   , inq_cnt
   , bbs_clsf
   , temp_yn
   , del_yn
	)
VALUES (
	  #{pstNo}
	, #{userId}
	, #{coId}
	, null
	, #{userId}
	, #{pstHd}
	, #{pstTtl}
	, #{pstCn}
	, SYSDATE
	, SYSDATE
	, #{inqCnt}
	, 'BB003'
	, 'N'
	, 'N'
	)

mapper.update:
UPDATE BBS_ATCH
SET FILE_EXIST = #{fileExist}
WHERE BBS_FL_NO = #{bbsFlNo}

UPDATE BBS_ATCH
SET FILE_EXIST = 'Y'
WHERE PST_NO = #{pstNo}

DELETE FROM BBS_ATCH
WHERE  PST_NO = #{pstNo}
AND    FILE_EXIST = 'N'

	UPDATE bbs
    SET
         USER_ID = #{userId}
        , CO_ID = #{coId}
        , MDFR_NM = #{userId}
        , PST_HD = #{pstHd}
        , PST_TTL = #{pstTtl}
        , PST_CN = #{pstCn}
        , MDFCN_DT = SYSDATE
        , INQ_CNT = #{inqCnt}
        , BBS_CLSF = 'BB003'
        , TEMP_YN = 'N'
        , DEL_YN = 'N'
	WHERE PST_NO = #{pstNo}

UPDATE BBS
SET
	DELR_NM=#{userId}
	, DEL_YN = 'Y'
	, DEL_DT = SYSDATE
WHERE PST_NO = #{pstNo}

