mapper.update:
UPDATE users
SET 
    HOBBY = #{hobby}
    , SPCABL = #{spcabl} 
    , USER_ZIP = #{userZip}
    , USER_ADDR = #{userAddr}
    , USER_DADDR = #{userDaddr}
WHERE user_id = #{userId}

mapper.insert:

SELECT NVL(MAX(CR_ACQS_NO), 1) + 1 FROM CRTFCT_ACQS

INSERT INTO CRTFCT_ACQS(
   CR_ACQS_NO
   , USER_ID
   , CRTFCT_NO
   , CRTFCT_NM
   , CRTFCT_ISSU_NO
   , CRTFCT_ACQS_YMD
   , CRTFCT_SCR
)
VALUES (
    #{crAcqsNo}
   ,#{userId}
   ,#{crtfctNo}
   ,#{crtfctNoNm}
   ,#{crtfctIssuNo}
   ,#{crtfctAcqsYmd}
   ,#{crtfctScr}

)

mapper.select:
SELECT user_id ,TO_CHAR(JNCMP_YMD,'YYYY') AS JOIN_YEAR
FROM EMP_INFO
WHERE user_id = #{userId}

