mapper.select:
SELECT A.memo_no , A.user_id , A.memo_ttl , A.memo_cn , A.dsptch_dt , A.stat_cd , A.stat_chg_dt , C.user_nm , C.user_img , G.dept_nm , F.cst_cd_nm , H.mng_cd_nm1 FROM memo_dsptch A INNER JOIN memo_rcptn B on B.memo_no = A.memo_no INNER JOIN users C on C.user_id = B.user_id INNER JOIN emp_info D on D.user_id = C.user_id INNER JOIN custome_code F on F.cst_cd = D.jbgd_cd INNER JOIN dept G on G.dept_no = D.dept_no INNER JOIN manage_code H on H.mng_cd = A.stat_cd WHERE 1=1
AND A.stat_cd = #{statCd}
AND A.user_id = #{userId}
ORDER BY A.memo_no DESC

mapper.insert:

SELECT NVL(MAX(memo_no),0)+1 FROM memo_dsptch

INSERT INTO memo_dsptch
(
  memo_no
, user_id
, memo_ttl
, memo_cn
, dsptch_dt
, stat_cd
, stat_chg_dt

) VALUES (
  #{memoNo}
, #{sender}
, #{memoTtl}
, #{memoCn}
, sysdate
, #{statCd}
, sysdate
)

mapper.select:
SELECT A.memo_no , A.user_id , A.stat_cd , A.stat_chg_dt , B.user_nm , B.user_img , C.memo_ttl , C.memo_cn , C.dsptch_dt , F.cst_cd_nm , G.dept_nm , H.mng_cd_nm1 FROM memo_rcptn A INNER JOIN users B on A.user_id = B.user_id INNER JOIN memo_dsptch C on A.memo_no = C.memo_no INNER JOIN emp_info D on A.user_id = D.user_id INNER JOIN custome_code F on D.jbgd_cd = F.cst_cd INNER JOIN dept G on G.dept_no = D.dept_no INNER JOIN manage_code H on H.mng_cd = A.stat_cd WHERE 1=1
AND A.stat_cd = #{statCd}
AND A.user_id = #{userId}
ORDER BY A.memo_no DESC

mapper.insert:


	SELECT 
		NVL(MAX(memo_no),0)+1 
	FROM
		memo_rcptn


INSERT INTO memo_rcptn (
  memo_no
, user_id
, stat_cd
, stat_chg_dt
) VALUES
(
  #{memoNo}
, #{userId}
, #{statCd}
, sysdate
)
