update.foreach:
INTO BBS_ATCH(
	BBS_FL_NO, FILE_NM, FILE_UUID, FILE_PATH, FILE_EXTN, FILE_SIZE, PST_NO, FILE_EXIST) 
VALUES(
	  #{bbsAtchVO.bbsFlNo}+#{idx}
	, #{bbsAtchVO.fileNm}
	, #{bbsAtchVO.fileUuid}
	, #{bbsAtchVO.filePath}
	, #{bbsAtchVO.fileExtn}
	, #{bbsAtchVO.fileSize}
	, #{bbsAtchVO.pstNo}
	, 'Y')

mapper.update:
UPDATE BBS_ATCH
SET FILE_EXIST = #{fileExist}
WHERE BBS_FL_NO = #{bbsFlNo}

UPDATE BBS_ATCH
SET FILE_EXIST = 'Y'
WHERE PST_NO = #{pstNo}

DELETE FROM BBS_ATCH
WHERE  PST_NO = #{pstNo}
AND    FILE_EXIST = 'N'

mapper.insert:

		SELECT SEQ_BBS.NEXTVAL FROM DUAL

 	INSERT INTO BBS(
