mapper.select:
	SELECT
        NVL(COUNT(rspns_ct),0)
    FROM
        srvy_rspns
	WHERE
		srvy_no = #{srvyNo}
	AND
		qitem_no = #{qitemNo}
	AND
		rspns_ct = #{optnNo}

mapper.insert:
INSERT INTO srvy (
srvy_no
,user_id
,co_id
,srvy_ttl
,bgng_dt
,end_dt
,srvy_stts
,srvy_cn
,reg_dt
) VALUES (
(SELECT
NVL(MAX(srvy_no),0)+1 FROM srvy)
,#{userId}
,#{coId}
,#{srvyTtl}
,#{bgngDt}
,#{endDt}
,'SV001'
,#{srvyCn}
,sysdate
)

INSERT INTO srvy_qitem (
qitem_no
,srvy_no
,qitem_ct
,input_type
) VALUES 
(		
(SELECT NVL(MAX(qitem_no),0)+1 FROM srvy_qitem)
, (SELECT NVL(MAX(srvy_no),0) FROM srvy)
, #{qitemCt}
, #{inputType}
)
